import React, {Fragment, useEffect, useState} from 'react';
//useEffect will do a fetch request to the API every time this component is rendered

const ListTodos= () => {

    const [todos, setTodos] = useState([]);

    const getTodos = async() => {
        try {
            const response = await fetch ("http://localhost:5000/todos")
            const jsonData = await response.json();
            //have to parse first to get json data
            //using setTodos because that's the only way to change the state
            setTodos(jsonData);
        } catch (err) {
            console.error(err.message)
        }
    }

    useEffect(() => {
        getTodos();
    }, []);

    return (
        <Fragment>
            {" "}
            <table class="table mt-5 text-center">
    <thead>
      <tr>
        <th>Description</th>
        <th>Edit</th>
        <th>Delete</th>
      </tr>
    </thead>
    <tbody>
        {/* <tr>
        <td>John</td>
        <td>Doe</td>
        <td>john@example.com</td>
      </tr>*/}
      {todos.map(todo => (
        //todo_id is what makes each data unique
        <tr key={todo.todo_id}>
            <td>{todo.description}</td>
            <td>Edit</td>
            <td><button className="btn btn-danger">Delete</button></td>
        </tr>
      ))}

    </tbody>
  </table>
        </Fragment>
    )
}

export default ListTodos;
